$includesDir = __DIR__ . '/includes';
if (is_dir($includesDir)) {
    $files = [];
    
    foreach (scandir($includesDir) as $file) {
        if (strpos($file, '.php') !== false) {
            $files[$file] = $includesDir . '/' . $file;
        }
    }
    
    if ($files) {
        sort($files);
        
        foreach ($files as $file) {
            include $file;
        }
    }
}
